<?php
namespace Moto\System\Backup; use Moto; use Carbon\Carbon; final class BackupStepResult { protected $_attributes = [ 'completed' => false, 'needRepeat' => false, 'isError' => true, ]; public function __construct(array $attributes = []) { $this->_attributes = array_merge($this->_attributes, $attributes); } public function toArray() { return $this->_attributes; } public function getAttribute($name, $default = null) { if (array_key_exists($name, $this->_attributes)) { return $this->_attributes[$name]; } return $default; } public function isCompleted() { return $this->getAttribute('completed'); } public function isNeedRepeat() { return $this->getAttribute('needRepeat'); } public function getData() { return $this->getAttribute('data'); } public function isError() { return $this->getAttribute('isError'); } public function getError() { return $this->getAttribute('error'); } } 